import React from 'react';

const ProductCard = ({ product, onClick, isPinned, onTogglePin }) => {
  const handlePinClick = (e) => {
    e.stopPropagation(); // Evita che il click si propaghi alla card
    onTogglePin();
  };

  // Utilizzo un'immagine di placeholder se non ci sono immagini disponibili
  const imageSrc = product.image_urls && product.image_urls.length > 0
    ? product.image_urls[0]
    : 'https://via.placeholder.com/300x200?text=Immagine+non+disponibile';

  return (
    <div 
      className="product-card relative cursor-pointer transform transition-transform hover:scale-[1.02]"
      onClick={onClick}
    >
      <div className="relative">
        <img 
          src={imageSrc} 
          alt={product.name} 
          className="product-image"
          onError={(e) => {
            e.target.src = 'https://via.placeholder.com/300x200?text=Errore+immagine';
          }}
        />
        <button 
          className={`pin-button ${isPinned ? 'bg-red-600' : 'bg-gray-600 bg-opacity-70'}`}
          onClick={handlePinClick}
          aria-label={isPinned ? "Rimuovi dalla moodboard" : "Aggiungi alla moodboard"}
        >
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-4 h-4">
            <path fillRule="evenodd" d="M5.25 12a.75.75 0 01.75-.75h12a.75.75 0 010 1.5H6a.75.75 0 01-.75-.75z" clipRule="evenodd" />
            {!isPinned && <path fillRule="evenodd" d="M12 5.25a.75.75 0 01.75.75v12a.75.75 0 01-1.5 0V6a.75.75 0 01.75-.75z" clipRule="evenodd" />}
          </svg>
        </button>
      </div>

      <div className="product-info">
        <h3 className="text-lg font-semibold text-gray-800 mb-1">{product.name}</h3>
        <p className="text-sm text-gray-600 mb-2">{product.manufacturer}</p>
        <p className="text-sm text-gray-700 line-clamp-2">{product.short_description}</p>

        <div className="mt-3 flex justify-between items-center">
          <span className="inline-block bg-gray-200 text-gray-800 text-xs px-2 py-1 rounded">
            {product.category}
          </span>
          <span className="text-primary-600 text-sm font-medium">Dettagli â†’</span>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
