import React from 'react';
import ReactMarkdown from 'react-markdown';

const ChatMessage = ({ message, isLastMessage }) => {
  const isUser = message.role === 'user';

  return (
    <div className={`message ${isUser ? 'message-user' : 'message-bot'}`}>
      {!isUser && (
        <div className="flex items-center mb-1">
          <div className="w-6 h-6 rounded-full bg-primary-600 text-white flex items-center justify-center mr-2">
            <span className="text-xs font-bold">AI</span>
          </div>
          <span className="text-sm font-medium text-gray-700">Architetto AI</span>
        </div>
      )}

      {isUser ? (
        <p className="whitespace-pre-wrap">{message.content}</p>
      ) : (
        <div className="prose prose-sm max-w-none">
          <ReactMarkdown>{message.content}</ReactMarkdown>
        </div>
      )}
    </div>
  );
};

export default ChatMessage;
